let questionNumber = 0;

let points = 0;

let selectedAns = "";

var questionObj;
let totalQuestionNum = 10;

//this function displays the question + answer choices
function displayQuestion() {
  const youScored = document.querySelector("#quest");
  youScored.textContent = "Question";

  const questionSpace = document.querySelector("#btn");
  questionSpace.textContent = "NEXT QUESTION!!";

  const end = document.querySelector("#correct");
  end.textContent = " ";

  const ansChoices = document.querySelector("#ans-choices");
  ansChoices.style.visibility = "visible";

  if (questionNumber >= totalQuestionNum) {
    endScreen();
  } else {
    document.getElementById("ans1").disabled = false;
    document.getElementById("ans2").disabled = false;
    document.getElementById("ans3").disabled = false;

    //stores question/answer choices in a variable
    this.questionObj = generate();
    const questionSpace = document.querySelector("#question");
    questionSpace.textContent = questionObj.question;

    const imgSpace = document.getElementById("image");
    imgSpace.innerHTML = "";

    var elem = document.createElement("img");
    elem.setAttribute("src", questionObj.imgsrc);
    imgSpace.appendChild(elem);

    const ansSpace1 = document.querySelector("#ans1");
    ansSpace1.textContent = questionObj.answerChoice1;
    const ansSpace2 = document.querySelector("#ans2");
    ansSpace2.textContent = questionObj.answerChoice2;
    const ansSpace3 = document.querySelector("#ans3");
    ansSpace3.textContent = questionObj.answerChoice3;
  }
}

function submit(ansNum) {
  document.getElementById("ans1").disabled = true;
  document.getElementById("ans2").disabled = true;
  document.getElementById("ans3").disabled = true;

  let ans = "";
  switch (ansNum) {
    case 0:
      ans = questionObj.answerChoice1;
      break;
    case 1:
      ans = questionObj.answerChoice2;
      break;
    case 2:
      ans = questionObj.answerChoice3;
      break;
  }

  const cor = document.querySelector("#correct");

  if (questionObj.correctAns == ans) {
    points++;
    cor.textContent = "Awesome! You are correct!";
  } else {
    cor.textContent = "You are wrong, sorry!";
  }

  questionNumber++;
}

